import { Sidebar } from "@/components/layout/Sidebar";
import { Menu, Bell, Search, Trash2, ChevronUp, ChevronDown, ChevronsUpDown } from "lucide-react";
import { useState, useMemo } from "react";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { ResponsivePagination } from "@/components/dashboard/ResponsivePagination";

const mockItems = [
  { id: 1, name: "Web Development", price: 100, description: "Professional web development services" },
  { id: 2, name: "Mobile App", price: 200, description: "Cross-platform mobile application" },
  { id: 3, name: "Consulting", price: 150, description: "Business consulting hours" },
  { id: 4, name: "Design Services", price: 250, description: "UI/UX design and branding" },
  { id: 5, name: "Hosting", price: 50, description: "Annual hosting package" },
];

type SortField = "name" | "price";
type SortOrder = "asc" | "desc";

export default function Items() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [itemsList, setItemsList] = useState(mockItems);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState<number | null>(null);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [formData, setFormData] = useState({ name: "", price: "", description: "" });
  const [formErrors, setFormErrors] = useState<Record<string, string>>({});
  const [searchTerm, setSearchTerm] = useState("");
  const [sortField, setSortField] = useState<SortField>("name");
  const [sortOrder, setSortOrder] = useState<SortOrder>("asc");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const filteredData = useMemo(() => {
    let result = itemsList.filter(item =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    result.sort((a, b) => {
      let aVal = a[sortField];
      let bVal = b[sortField];
      if (typeof aVal === "string") aVal = aVal.toLowerCase();

      if (aVal < bVal) return sortOrder === "asc" ? -1 : 1;
      if (aVal > bVal) return sortOrder === "asc" ? 1 : -1;
      return 0;
    });

    return result;
  }, [itemsList, searchTerm, sortField, sortOrder]);

  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const handleDelete = () => {
    if (selectedItemId) {
      setItemsList(itemsList.filter(item => item.id !== selectedItemId));
      setDeleteDialogOpen(false);
      setSelectedItemId(null);
    }
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};
    if (!formData.name) newErrors.name = "Name is required";
    if (!formData.price) newErrors.price = "Price is required";
    else if (parseFloat(formData.price) <= 0) newErrors.price = "Price must be greater than 0";
    setFormErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleCreate = () => {
    if (validateForm()) {
      setItemsList([...itemsList, {
        id: Math.max(...itemsList.map(i => i.id)) + 1,
        name: formData.name,
        price: parseFloat(formData.price),
        description: formData.description
      }]);
      setFormData({ name: "", price: "", description: "" });
      setFormErrors({});
      setCreateDialogOpen(false);
      setCurrentPage(1);
    }
  };

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) return <ChevronsUpDown className="h-4 w-4 opacity-40" />;
    return sortOrder === "asc" ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />;
  };

  return (
    <div className="min-h-screen bg-background font-sans flex">
      <div className="hidden md:block">
        <Sidebar />
      </div>

      <div className="flex-1 md:ml-72 flex flex-col min-h-screen transition-all duration-300">
        <header className="h-20 border-b border-border/40 bg-white/50 backdrop-blur-md sticky top-0 z-40 px-6 flex items-center justify-between">
          <div className="flex items-center gap-4 md:hidden">
            <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
              <SheetTrigger asChild>
                <button className="p-2 hover:bg-muted rounded-md">
                  <Menu className="h-6 w-6 text-foreground" />
                </button>
              </SheetTrigger>
              <SheetContent side="left" className="p-0 w-72 border-r-0 bg-transparent">
                <Sidebar />
              </SheetContent>
            </Sheet>
            <div className="font-bold font-heading text-lg">SimpleBit</div>
          </div>

          <div className="hidden md:flex flex-col">
            <h1 className="text-xl font-bold font-heading text-foreground">Items & Taxes</h1>
            <p className="text-muted-foreground text-xs">Manage your catalog and taxes</p>
          </div>

          <div className="flex items-center gap-4">
            <button className="relative p-2.5 hover:bg-muted rounded-full transition-colors group">
              <Bell className="h-5 w-5 text-muted-foreground group-hover:text-primary transition-colors" />
            </button>
          </div>
        </header>

        <main className="flex-1 p-6 md:p-8 max-w-5xl mx-auto w-full space-y-6">
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-2xl font-bold font-heading">Items</h2>
              <p className="text-muted-foreground text-sm mt-1">Manage your catalog. Taxes are global and set by admin.</p>
            </div>
            <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-primary hover:bg-primary/90" data-testid="button-create-item-trigger">
                  Create Item
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-md">
                <DialogHeader>
                  <DialogTitle>Create New Item</DialogTitle>
                  <DialogDescription>Add a new item to your catalog</DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="name" className="text-sm font-medium">Name *</Label>
                    <Input
                      id="name"
                      placeholder="Item name"
                      className="border-2 border-border/50 focus:border-primary/50"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      data-testid="input-item-name"
                    />
                    {formErrors.name && <p className="text-xs text-destructive">{formErrors.name}</p>}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="price" className="text-sm font-medium">Base Price (AED) *</Label>
                    <Input
                      id="price"
                      type="number"
                      placeholder="0.00"
                      className="border-2 border-border/50 focus:border-primary/50"
                      value={formData.price}
                      onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                      data-testid="input-item-price"
                    />
                    {formErrors.price && <p className="text-xs text-destructive">{formErrors.price}</p>}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="description" className="text-sm font-medium">Description</Label>
                    <Textarea
                      id="description"
                      placeholder="Item description (optional)"
                      className="border-2 border-border/50 focus:border-primary/50 resize-none"
                      rows={3}
                      value={formData.description}
                      onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                      data-testid="textarea-item-description"
                    />
                  </div>
                  <div className="flex gap-3 justify-end pt-2">
                    <Button variant="outline" onClick={() => setCreateDialogOpen(false)}>Cancel</Button>
                    <Button className="bg-primary hover:bg-primary/90" onClick={handleCreate} data-testid="button-confirm-create-item">
                      Create Item
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>

          {/* Search Bar */}
          <div className="relative">
            <Search className="absolute left-3 top-3 h-5 w-5 text-muted-foreground" />
            <Input
              placeholder="Search items by name..."
              className="pl-10 h-11 border-2 border-border/50 bg-background/50 focus:border-primary/50 transition-colors"
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                setCurrentPage(1);
              }}
              data-testid="input-search-items"
            />
          </div>

          {/* Items Table */}
          <div className="border border-border/50 rounded-xl overflow-hidden bg-card shadow-sm">
            <div className="w-full overflow-x-auto scrollbar-hide">
              <div className="min-w-full">
                <Table>
                  <TableHeader>
                    <TableRow className="border-border/50 bg-muted/30 hover:bg-muted/30">
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors text-xs sm:text-sm" 
                        onClick={() => handleSort("name")}
                        data-testid="header-name"
                      >
                        <div className="flex items-center gap-1">
                          Name
                          <SortIcon field="name" />
                        </div>
                      </TableHead>
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors text-right text-xs sm:text-sm" 
                        onClick={() => handleSort("price")}
                        data-testid="header-price"
                      >
                        <div className="flex items-center gap-1 justify-end">
                          Price
                          <SortIcon field="price" />
                        </div>
                      </TableHead>
                      <TableHead className="font-semibold text-foreground text-right text-xs sm:text-sm">Action</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {paginatedData.map((item) => (
                      <TableRow key={item.id} className="border-border/50 hover:bg-muted/20 transition-colors" data-testid={`row-item-${item.id}`}>
                        <TableCell className="font-medium text-foreground text-xs sm:text-sm">{item.name}</TableCell>
                        <TableCell className="text-right text-foreground font-medium text-xs sm:text-sm">{item.price.toFixed(2)} AED</TableCell>
                        <TableCell className="text-right">
                          <Dialog open={deleteDialogOpen && selectedItemId === item.id} onOpenChange={(open) => {
                            setDeleteDialogOpen(open);
                            if (!open) setSelectedItemId(null);
                          }}>
                            <DialogTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                className="h-7 text-destructive hover:bg-destructive/10 hover:text-destructive"
                                onClick={() => setSelectedItemId(item.id)}
                                data-testid={`button-delete-item-${item.id}`}
                              >
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Delete Item</DialogTitle>
                                <DialogDescription>Are you sure you want to delete this item? This action cannot be undone.</DialogDescription>
                              </DialogHeader>
                              <div className="flex gap-3 justify-end">
                                <Button variant="outline" onClick={() => { setDeleteDialogOpen(false); setSelectedItemId(null); }}>Cancel</Button>
                                <Button className="bg-destructive hover:bg-destructive/90" onClick={handleDelete}>Delete</Button>
                              </div>
                            </DialogContent>
                          </Dialog>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            </div>
          </div>

          {/* Pagination */}
          <ResponsivePagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
            itemsShowing={paginatedData.length}
            totalItems={filteredData.length}
          />

          {/* Global Taxes */}
          <Card className="border-2 border-border/50 bg-muted/30">
            <CardHeader>
              <CardTitle className="text-lg">Global Taxes</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="flex justify-between items-center py-2">
                <span className="text-foreground font-medium">VAT</span>
                <span className="text-foreground font-semibold">5.00%</span>
              </div>
              <div className="border-t border-border/50" />
              <div className="flex justify-between items-center py-2">
                <span className="text-foreground font-medium">Municipality (optional)</span>
                <span className="text-foreground font-semibold">5.00%</span>
              </div>
              <p className="text-xs text-muted-foreground mt-4">Municipality tax can be toggled per invoice.</p>
            </CardContent>
          </Card>
        </main>
      </div>
    </div>
  );
}

client/src/pages/create-invoice.tsx.content:
import { Sidebar } from "@/components/layout/Sidebar";
import { Menu, Bell, X } from "lucide-react";
import { useState } from "react";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { Link, useLocation } from "wouter";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

const mockItems = [
  { id: 1, name: "Web Development", price: 100 },
  { id: 2, name: "Mobile App", price: 200 },
  { id: 3, name: "Consulting", price: 150 },
  { id: 4, name: "Design Services", price: 250 },
  { id: 5, name: "Hosting", price: 50 },
];

export default function CreateInvoice() {
  const [, setLocation] = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [items, setItems] = useState([{ itemId: "", quantity: "1" }]);
  const [municipalityTax, setMunicipalityTax] = useState(false);
  const [customerPaysFee, setCustomerPaysFee] = useState(true);
  const [customerEmail, setCustomerEmail] = useState("");
  const [expiresIn, setExpiresIn] = useState("24");
  const [errors, setErrors] = useState<Record<string, string>>({});

  const addItem = () => {
    setItems([...items, { itemId: "", quantity: "1" }]);
  };

  const removeItem = (index: number) => {
    if (items.length > 1) {
      setItems(items.filter((_, i) => i !== index));
    }
  };

  const updateItem = (index: number, field: string, value: string) => {
    const updated = [...items];
    updated[index] = { ...updated[index], [field]: value };
    setItems(updated);
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};
    const selectedItems = items.filter(item => item.itemId);
    if (selectedItems.length === 0) newErrors.items = "Please select at least one item";
    if (customerEmail && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(customerEmail)) newErrors.email = "Invalid email format";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleCreate = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      // Redirect to success page
      setLocation("/invoice-success");
    }
  };

  return (
    <div className="min-h-screen bg-background font-sans flex">
      <div className="hidden md:block">
        <Sidebar />
      </div>

      <div className="flex-1 md:ml-72 flex flex-col min-h-screen transition-all duration-300">
        <header className="h-20 border-b border-border/40 bg-white/50 backdrop-blur-md sticky top-0 z-40 px-6 flex items-center justify-between">
          <div className="flex items-center gap-4 md:hidden">
            <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
              <SheetTrigger asChild>
                <button className="p-2 hover:bg-muted rounded-md">
                  <Menu className="h-6 w-6 text-foreground" />
                </button>
              </SheetTrigger>
              <SheetContent side="left" className="p-0 w-72 border-r-0 bg-transparent">
                <Sidebar />
              </SheetContent>
            </Sheet>
            <div className="font-bold font-heading text-lg">SimpleBit</div>
          </div>

          <div className="hidden md:flex flex-col">
            <h1 className="text-xl font-bold font-heading text-foreground">Create Invoice</h1>
            <p className="text-muted-foreground text-xs">Build a new invoice with items</p>
          </div>

          <div className="flex items-center gap-4">
            <button className="relative p-2.5 hover:bg-muted rounded-full transition-colors group">
              <Bell className="h-5 w-5 text-muted-foreground group-hover:text-primary transition-colors" />
            </button>
          </div>
        </header>

        <main className="flex-1 p-6 md:p-8 max-w-2xl mx-auto w-full">
          <div className="mb-6">
            <h2 className="text-2xl font-bold font-heading">Create Invoice</h2>
            <p className="text-muted-foreground text-sm mt-1">Select items to build an invoice. VAT always included, Municipality optional.</p>
          </div>

          <Card className="border-2 border-border/50">
            <CardHeader>
              <CardTitle>Select Items</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Item Rows */}
              <div className="space-y-3">
                {items.map((item, index) => (
                  <div key={index} className="flex gap-3 items-end p-4 bg-muted/20 rounded-lg border border-border/30">
                    <div className="flex-1">
                      <Label className="text-xs font-semibold text-foreground mb-2 block">Item</Label>
                      <Select value={item.itemId} onValueChange={(val) => updateItem(index, "itemId", val)}>
                        <SelectTrigger className="border-2 border-border/50 focus:border-primary/50 h-10" data-testid={`select-item-${index}`}>
                          <SelectValue placeholder="Select item" />
                        </SelectTrigger>
                        <SelectContent>
                          {mockItems.map((opt) => (
                            <SelectItem key={opt.id} value={opt.id.toString()}>
                              {opt.name} - {opt.price} AED
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="w-24">
                      <Label className="text-xs font-semibold text-foreground mb-2 block">Qty</Label>
                      <Input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => updateItem(index, "quantity", e.target.value)}
                        className="border-2 border-border/50 focus:border-primary/50 h-10"
                        data-testid={`input-quantity-${index}`}
                      />
                    </div>
                    {items.length > 1 && (
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => removeItem(index)}
                        className="text-destructive hover:bg-destructive/10 h-10 w-10"
                        data-testid={`button-remove-item-${index}`}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    )}
                  </div>
                ))}
              </div>

              {/* Add Item Button */}
              <Button
                variant="outline"
                onClick={addItem}
                className="w-full border-2 border-primary text-primary hover:bg-primary/5 h-10"
                data-testid="button-add-item"
              >
                + Add Item
              </Button>

              {/* Checkboxes */}
              <div className="space-y-3 pt-2 border-t border-border/50">
                <div className="flex items-center gap-3">
                  <Checkbox
                    id="municipality"
                    checked={municipalityTax}
                    onCheckedChange={(checked) => setMunicipalityTax(checked as boolean)}
                    data-testid="checkbox-municipality"
                  />
                  <Label htmlFor="municipality" className="text-sm font-medium cursor-pointer">Apply Municipality Tax (5.00%)</Label>
                </div>
                <div className="flex items-center gap-3">
                  <Checkbox
                    id="service-fee"
                    checked={customerPaysFee}
                    onCheckedChange={(checked) => setCustomerPaysFee(checked as boolean)}
                    data-testid="checkbox-service-fee"
                  />
                  <Label htmlFor="service-fee" className="text-sm font-medium cursor-pointer">Customer Pays Service Fee</Label>
                </div>
              </div>

              {/* Customer Email */}
              <div className="space-y-2 pt-2">
                <Label htmlFor="email" className="text-sm font-medium">Customer Email</Label>
                <Input
                  id="email"
                  type="email"
                  placeholder="customer@example.com"
                  className="border-2 border-border/50 focus:border-primary/50 h-10"
                  value={customerEmail}
                  onChange={(e) => setCustomerEmail(e.target.value)}
                  data-testid="input-customer-email"
                />
                <p className="text-xs text-muted-foreground">If provided, the customer will receive payment notifications</p>
              </div>

              {/* Expires In */}
              <div className="space-y-2">
                <Label htmlFor="expires" className="text-sm font-medium">Expires In</Label>
                <Select value={expiresIn} onValueChange={setExpiresIn}>
                  <SelectTrigger className="border-2 border-border/50 focus:border-primary/50 h-10" data-testid="select-expires">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="24">24 Hours</SelectItem>
                    <SelectItem value="48">48 Hours</SelectItem>
                    <SelectItem value="7">7 Days</SelectItem>
                    <SelectItem value="30">30 Days</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-xs text-muted-foreground">Invoice will automatically expire after this duration</p>
              </div>

              {errors.items && <p className="text-xs text-destructive">{errors.items}</p>}
              {errors.email && <p className="text-xs text-destructive">{errors.email}</p>}

              {/* Action Buttons */}
              <div className="flex gap-3 pt-2">
                <Link href="/invoices" className="flex-1">
                  <Button variant="outline" className="w-full border-2 h-10" data-testid="button-cancel">
                    Cancel
                  </Button>
                </Link>
                <Button onClick={handleCreate} className="flex-1 bg-primary hover:bg-primary/90 h-10" data-testid="button-create">
                  Create Invoice
                </Button>
              </div>
            </CardContent>
          </Card>
        </main>
      </div>
    </div>
  );
}

client/src/pages/signup.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Link } from "wouter";
import { Mail, Lock, User, Phone } from "lucide-react";

const countries = ["United Arab Emirates", "Saudi Arabia", "Kuwait", "Qatar", "Bahrain", "Oman"];

export default function Signup() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    country: "",
    password: "",
    confirmPassword: "",
  });
  const [errors, setErrors] = useState<Record<string, string>>({});

  const validate = () => {
    const newErrors: Record<string, string> = {};
    if (!formData.firstName) newErrors.firstName = "First name is required";
    if (!formData.lastName) newErrors.lastName = "Last name is required";
    if (!formData.email) newErrors.email = "Email is required";
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = "Invalid email format";
    if (!formData.phone) newErrors.phone = "Phone number is required";
    if (!formData.country) newErrors.country = "Country is required";
    if (!formData.password) newErrors.password = "Password is required";
    else if (formData.password.length < 8) newErrors.password = "Password must be at least 8 characters";
    if (!formData.confirmPassword) newErrors.confirmPassword = "Please confirm password";
    else if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Passwords do not match";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validate()) {
      setFormData({ firstName: "", lastName: "", email: "", phone: "", country: "", password: "", confirmPassword: "" });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-background to-muted flex items-center justify-center p-4">
      <Card className="w-full max-w-md border-2 border-border/50">
        <CardHeader className="space-y-2 text-center pb-6">
          <div className="flex justify-center mb-4">
            <div className="h-12 w-12 rounded-xl bg-primary flex items-center justify-center shadow-lg shadow-primary/30">
              <span className="text-white font-bold text-2xl">S</span>
            </div>
          </div>
          <CardTitle className="text-2xl font-heading">Create Account</CardTitle>
          <CardDescription>Sign up as a SimpleBit merchant</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <form onSubmit={handleSubmit} className="space-y-3">
            <div className="grid grid-cols-2 gap-3">
              <div className="space-y-1">
                <Label htmlFor="firstName" className="text-xs font-medium">First Name *</Label>
                <Input
                  id="firstName"
                  placeholder="John"
                  className="h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.firstName}
                  onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                  data-testid="input-first-name"
                />
                {errors.firstName && <p className="text-xs text-destructive">{errors.firstName}</p>}
              </div>
              <div className="space-y-1">
                <Label htmlFor="lastName" className="text-xs font-medium">Last Name *</Label>
                <Input
                  id="lastName"
                  placeholder="Doe"
                  className="h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.lastName}
                  onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                  data-testid="input-last-name"
                />
                {errors.lastName && <p className="text-xs text-destructive">{errors.lastName}</p>}
              </div>
            </div>

            <div className="space-y-1">
              <Label htmlFor="email" className="text-xs font-medium">Email Address *</Label>
              <div className="relative">
                <Mail className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  id="email"
                  type="email"
                  placeholder="you@example.com"
                  className="pl-9 h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  data-testid="input-signup-email"
                />
              </div>
              {errors.email && <p className="text-xs text-destructive">{errors.email}</p>}
            </div>

            <div className="space-y-1">
              <Label htmlFor="phone" className="text-xs font-medium">Phone Number *</Label>
              <div className="relative">
                <Phone className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  id="phone"
                  placeholder="+971234567890"
                  className="pl-9 h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  data-testid="input-phone"
                />
              </div>
              {errors.phone && <p className="text-xs text-destructive">{errors.phone}</p>}
            </div>

            <div className="space-y-1">
              <Label htmlFor="country" className="text-xs font-medium">Country *</Label>
              <Select value={formData.country} onValueChange={(val) => setFormData({ ...formData, country: val })}>
                <SelectTrigger className="h-10 border-2 border-border/50 focus:border-primary/50 text-sm" data-testid="select-country">
                  <SelectValue placeholder="Select country" />
                </SelectTrigger>
                <SelectContent>
                  {countries.map((c) => (
                    <SelectItem key={c} value={c}>{c}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.country && <p className="text-xs text-destructive">{errors.country}</p>}
            </div>

            <div className="space-y-1">
              <Label htmlFor="password" className="text-xs font-medium">Password *</Label>
              <div className="relative">
                <Lock className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  id="password"
                  type="password"
                  placeholder=""
                  className="pl-9 h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                  data-testid="input-signup-password"
                />
              </div>
              {errors.password && <p className="text-xs text-destructive">{errors.password}</p>}
            </div>

            <div className="space-y-1">
              <Label htmlFor="confirmPassword" className="text-xs font-medium">Confirm Password *</Label>
              <div className="relative">
                <Lock className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  id="confirmPassword"
                  type="password"
                  placeholder=""
                  className="pl-9 h-10 border-2 border-border/50 focus:border-primary/50 text-sm"
                  value={formData.confirmPassword}
                  onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                  data-testid="input-confirm-password"
                />
              </div>
              {errors.confirmPassword && <p className="text-xs text-destructive">{errors.confirmPassword}</p>}
            </div>

            <Button type="submit" className="w-full bg-primary hover:bg-primary/90 h-10 text-base mt-2" data-testid="button-create-account">
              Create Account
            </Button>
          </form>

          <div className="text-center text-sm">
            <span className="text-muted-foreground">Already have an account? </span>
            <Link href="/login" asChild>
              <a className="text-primary hover:underline font-medium" data-testid="link-login">
                Sign In
              </a>
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

script/build.ts.content:
import { build as esbuild } from "esbuild";
import { build as viteBuild } from "vite";
import { rm, readFile } from "fs/promises";

// server deps to bundle to reduce openat(2) syscalls
// which helps cold start times
const allowlist = [
  "@google/generative-ai",
  "axios",
  "connect-pg-simple",
  "cors",
  "date-fns",
  "drizzle-orm",
  "drizzle-zod",
  "express",
  "express-rate-limit",
  "express-session",
  "jsonwebtoken",
  "memorystore",
  "multer",
  "nanoid",
  "nodemailer",
  "openai",
  "passport",
  "passport-local",
  "pg",
  "stripe",
  "uuid",
  "ws",
  "xlsx",
  "zod",
  "zod-validation-error",
];

async function buildAll() {
  await rm("dist", { recursive: true, force: true });

  console.log("building client...");
  await viteBuild({ base: "/simplebit/" });

  console.log("building server...");
  const pkg = JSON.parse(await readFile("package.json", "utf-8"));
  const allDeps = [
    ...Object.keys(pkg.dependencies || {}),
    ...Object.keys(pkg.devDependencies || {}),
  ];
  const externals = allDeps.filter((dep) => !allowlist.includes(dep));

  await esbuild({
    entryPoints: ["server/index.ts"],
    platform: "node",
    bundle: true,
    format: "cjs",
    outfile: "dist/index.cjs",
    define: {
      "process.env.NODE_ENV": '"production"',
    },
    minify: true,
    external: externals,
    logLevel: "info",
  });
}

buildAll().catch((err) => {
  console.error(err);
  process.exit(1);
});

client/src/pages/transactions.tsx.content:
import { Sidebar } from "@/components/layout/Sidebar";
import { Menu, Bell, Search, ChevronUp, ChevronDown, ChevronsUpDown } from "lucide-react";
import { useState, useMemo } from "react";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { ResponsivePagination } from "@/components/dashboard/ResponsivePagination";

const mockTransactions = [
  { id: 1, transaction: "#29", invoice: "#INV-175829...", amount: 57.75, crypto: "USDT:BSC", status: "Finished", date: "Sep 19, 2025, 05:24 AM" },
  { id: 2, transaction: "#17", invoice: "#INV-175751...", amount: 105, crypto: "BNB:BSC", status: "Expired", date: "Sep 19, 2025, 01:27 PM" },
  { id: 3, transaction: "#16", invoice: "#INV-175750...", amount: 52.50, crypto: "USDT:BSC", status: "Finished", date: "Sep 19, 2025, 01:13 PM" },
  { id: 4, transaction: "#15", invoice: "#INV-175649...", amount: 200, crypto: "ETH:BSC", status: "Finished", date: "Sep 18, 2025, 08:00 PM" },
  { id: 5, transaction: "#14", invoice: "#INV-175648...", amount: 75.50, crypto: "USDT:BSC", status: "Expired", date: "Sep 18, 2025, 17:44 PM" },
];

const statusColors = {
  Finished: "bg-emerald-500/10 text-emerald-700 border-emerald-500/20",
  Expired: "bg-red-500/10 text-red-700 border-red-500/20",
  Failed: "bg-red-500/10 text-red-700 border-red-500/20",
  Pending: "bg-amber-500/10 text-amber-700 border-amber-500/20",
};

type SortField = "transaction" | "amount" | "status" | "date";
type SortOrder = "asc" | "desc";

export default function Transactions() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [sortField, setSortField] = useState<SortField>("date");
  const [sortOrder, setSortOrder] = useState<SortOrder>("desc");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const filteredData = useMemo(() => {
    let result = mockTransactions.filter(tx => {
      const matchesSearch = tx.transaction.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           tx.invoice.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           tx.amount.toString().toLowerCase().includes(searchTerm.toLowerCase()) ||
                           tx.crypto.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           tx.status.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           tx.date.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStatus = statusFilter === "all" || tx.status.toLowerCase() === statusFilter.toLowerCase();
      return matchesSearch && matchesStatus;
    });

    result.sort((a, b) => {
      let aVal = a[sortField];
      let bVal = b[sortField];
      if (typeof aVal === "string") aVal = aVal.toLowerCase();

      if (aVal < bVal) return sortOrder === "asc" ? -1 : 1;
      if (aVal > bVal) return sortOrder === "asc" ? 1 : -1;
      return 0;
    });

    return result;
  }, [searchTerm, statusFilter, sortField, sortOrder]);

  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) return <ChevronsUpDown className="h-4 w-4 opacity-40" />;
    return sortOrder === "asc" ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />;
  };

  return (
    <div className="min-h-screen bg-background font-sans flex">
      <div className="hidden md:block">
        <Sidebar />
      </div>

      <div className="flex-1 md:ml-72 flex flex-col min-h-screen transition-all duration-300">
        <header className="h-20 border-b border-border/40 bg-white/50 backdrop-blur-md sticky top-0 z-40 px-6 flex items-center justify-between">
          <div className="flex items-center gap-4 md:hidden">
            <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
              <SheetTrigger asChild>
                <button className="p-2 hover:bg-muted rounded-md">
                  <Menu className="h-6 w-6 text-foreground" />
                </button>
              </SheetTrigger>
              <SheetContent side="left" className="p-0 w-72 border-r-0 bg-transparent">
                <Sidebar />
              </SheetContent>
            </Sheet>
            <div className="font-bold font-heading text-lg">SimpleBit</div>
          </div>

          <div className="hidden md:flex flex-col">
            <h1 className="text-xl font-bold font-heading text-foreground">Transactions</h1>
            <p className="text-muted-foreground text-xs">Track your transaction history</p>
          </div>

          <div className="flex items-center gap-4">
            <button className="relative p-2.5 hover:bg-muted rounded-full transition-colors group">
              <Bell className="h-5 w-5 text-muted-foreground group-hover:text-primary transition-colors" />
            </button>
          </div>
        </header>

        <main className="flex-1 p-6 md:p-8 max-w-7xl mx-auto w-full space-y-6">
          <div>
            <h2 className="text-2xl font-bold font-heading">Transactions</h2>
            <p className="text-muted-foreground text-sm mt-1">View your transaction history and payout records</p>
          </div>

          {/* Available Balance */}
          <Card className="border-2 border-primary/20 bg-gradient-to-br from-primary/5 to-primary/10">
            <CardContent className="pt-6">
              <div className="space-y-2">
                <p className="text-sm text-muted-foreground">Available Balance</p>
                <h3 className="text-3xl font-bold font-heading text-primary">41.50 AED</h3>
                <p className="text-sm text-muted-foreground">Funds available for withdrawal ( 11.00 AED pending )</p>
              </div>
            </CardContent>
          </Card>

          {/* Search and Filters */}
          <div className="space-y-4">
            <div className="relative">
              <Search className="absolute left-3 top-3 h-5 w-5 text-muted-foreground" />
              <Input
                placeholder="Search by transaction ID or invoice..."
                className="pl-10 h-11 border-2 border-border/50 bg-background/50 focus:border-primary/50 transition-colors"
                value={searchTerm}
                onChange={(e) => {
                  setSearchTerm(e.target.value);
                  setCurrentPage(1);
                }}
                data-testid="input-search-transactions"
              />
            </div>
            <Select value={statusFilter} onValueChange={(val) => {
              setStatusFilter(val);
              setCurrentPage(1);
            }}>
              <SelectTrigger className="w-full md:w-56 h-11 border-2 border-border/50 focus:border-primary/50" data-testid="select-status-filter">
                <SelectValue placeholder="All Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="finished">Finished</SelectItem>
                <SelectItem value="expired">Expired</SelectItem>
                <SelectItem value="failed">Failed</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Transactions Table */}
          <div className="border border-border/50 rounded-xl overflow-hidden bg-card shadow-sm">
            <div className="w-full overflow-x-auto scrollbar-hide">
              <div className="min-w-full">
                <Table>
                  <TableHeader>
                    <TableRow className="border-border/50 bg-muted/30 hover:bg-muted/30">
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors text-xs sm:text-sm" 
                        onClick={() => handleSort("transaction")}
                        data-testid="header-transaction"
                      >
                        <div className="flex items-center gap-1">
                          Transaction
                          <SortIcon field="transaction" />
                        </div>
                      </TableHead>
                      <TableHead className="font-semibold text-foreground hidden sm:table-cell text-xs sm:text-sm">Invoice</TableHead>
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors text-right text-xs sm:text-sm" 
                        onClick={() => handleSort("amount")}
                        data-testid="header-amount"
                      >
                        <div className="flex items-center gap-1 justify-end">
                          Amount
                          <SortIcon field="amount" />
                        </div>
                      </TableHead>
                      <TableHead className="font-semibold text-foreground hidden md:table-cell text-xs sm:text-sm">Crypto</TableHead>
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors text-xs sm:text-sm" 
                        onClick={() => handleSort("status")}
                        data-testid="header-status"
                      >
                        <div className="flex items-center gap-1">
                          Status
                          <SortIcon field="status" />
                        </div>
                      </TableHead>
                      <TableHead 
                        className="font-semibold text-foreground cursor-pointer hover:bg-muted/50 transition-colors hidden md:table-cell text-xs sm:text-sm" 
                        onClick={() => handleSort("date")}
                        data-testid="header-date"
                      >
                        <div className="flex items-center gap-1">
                          Date
                          <SortIcon field="date" />
                        </div>
                      </TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {paginatedData.map((tx) => (
                      <TableRow key={tx.id} className="border-border/50 hover:bg-muted/20 transition-colors" data-testid={`row-transaction-${tx.id}`}>
                        <TableCell className="font-medium text-foreground text-xs sm:text-sm">{tx.transaction}</TableCell>
                        <TableCell className="text-foreground/70 hidden sm:table-cell text-xs sm:text-sm truncate">{tx.invoice}</TableCell>
                        <TableCell className="text-right text-foreground font-medium text-xs sm:text-sm">{tx.amount.toFixed(2)} AED</TableCell>
                        <TableCell className="text-foreground/70 hidden md:table-cell text-xs">{tx.crypto}</TableCell>
                        <TableCell className="text-xs sm:text-sm">
                          <Badge className={`${statusColors[tx.status as keyof typeof statusColors]} border text-xs`}>
                            {tx.status}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-foreground/70 hidden md:table-cell text-xs">{tx.date}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            </div>
          </div>

          {/* Pagination */}
          <ResponsivePagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
            itemsShowing={paginatedData.length}
            totalItems={filteredData.length}
          />
        </main>
      </div>
    </div>
  );
}

