    setTimeout(() => setCopiedLink(false), 2000);
  };

  const handleSelectTip = (percentage: number) => {
    setSelectedTip(percentage.toString());
    setCustomTip("");
  };

  return (
    <div className="min-h-screen bg-background font-sans flex">
